import Vue from 'vue';
import Router from 'vue-router';
import Knights from './components/Knights/Knights.vue'
import Hall from './components/hall/Hall.vue'

Vue.use(Router);

export default new  Router({
    routes:[
        {
            path: '/knights',
            name: 'Knights',
            component: Knights
        },
        {
            path: '/hall',
            name: 'Hall',
            component: Hall
        }
    ]
})

